args:
@keyframes flag-wave {
  0% { transform: rotate(0deg) skewY(0deg) translateY(0); }
  25% { transform: rotate(3deg) skewY(2deg) translateY(-3px); }
  50% { transform: rotate(0deg) skewY(0deg) translateY(0); }
  75% { transform: rotate(-3deg) skewY(-2deg) translateY(3px); }
  100% { transform: rotate(0deg) skewY(0deg) translateY(0); }
}

.animate-flag {
  animation: flag-wave 3s ease-in-out infinite;
  filter: drop-shadow(0 4px 6px rgba(0, 0, 0, 0.3));
}
